import React, { Component } from "react";
import { Table, Card, Grid, StatsCard, Icon } from 'tabler-react';
import { ChevronsRight, ChevronsLeft } from 'react-feather';
import C3Chart from "react-c3js";
import 'c3/c3.css';
import './Dashboard.scss';

const data = {
    x: 'x',
    xFormat: '%Y-%m-%d',
    columns: [
        ['x', '2013-01-01', '2013-01-02', '2013-01-03', '2013-01-04', '2013-01-05', '2013-01-06'],
        ['data1', 30, 200, 100, 400, 150, 250],
        ['data2', 130, 340, 200, 500, 250, 350]
    ],
};

const axis = {
    x: {
        type: 'timeseries',
        tick: {
            format: '%Y-%m-%d'
        }
    }
};

class Overview extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            transactions: {
                amount: [],
                type: [],
                address: []
            }
        }
    }

    async componentDidMount() {
        console.log("mounted")
        try {
            let res = await fetch('/overview', {
                method: 'post',
                headers: {
                    'Accept': 'applcication/json',
                    'Content-Type': 'applcication/json'
                }
            });

            let result = await res.json();
            
            for (var i = 0; i < result.data.length; i++)
            {
                this.state.transactions.amount.push(result.data[i].amount)
                this.state.transactions.type.push(result.data[i].type)
                this.state.transactions.address.push(result.data[i].address)
            }

            console.log(result.data)
            console.log(this.state.transactions)
            this.forceUpdate();
        }
        catch (e) {
            console.log('exc')
            console.log(e)
        }

    }

    render() {
        return (
            <div className="dashboard">
                <div className="mainChart">
                    <div className="walletValue">
                        <Card
                            title="Wallet Value"
                            body={
                                <C3Chart data={data} axis={axis} />
                            }
                        />
                    </div>
                    <div className="transactions">
                        <Card>
                            <Table id="dynamictable"
                                highlightRowOnHover={true}
                                headerItems={[{ content: "Amount" }, { content: "Transaction Type" }, { content: "Address" }]}
                                bodyItems={[
                                    {
                                        key: 0,
                                        item: [
                                            { content: this.state.transactions.amount[0]},
                                            { content: this.state.transactions.type[0] ? <ChevronsLeft color="red" /> : <ChevronsLeft color="green" />},
                                            { content: this.state.transactions.address[0] },
                                        ],
                                    },
                                    {
                                        item: [
                                            { content: "1.25804" },
                                            { content: <ChevronsRight color="red" /> },
                                            { content: "bc1qm*hz0av" },
                                        ],
                                    },
                                    {
                                        item: [
                                            { content: "1.25804" },
                                            { content: "inc" },
                                            { content: "bc1qm*hz0av" },
                                        ],
                                    },
                                    {
                                        item: [
                                            { content: "1.25804" },
                                            { content: "inc" },
                                            { content: "bc1qm*hz0av" },
                                        ],
                                    },
                                    {
                                        item: [
                                            { content: "1.25804" },
                                            { content: "inc" },
                                            { content: "bc1qm*hz0av" },
                                        ],
                                    },
                                    {
                                        item: [
                                            { content: "1.25804" },
                                            { content: "inc" },
                                            { content: "bc1qm*hz0av" },
                                        ],
                                    },
                                    {
                                        item: [
                                            { content: "1.25804" },
                                            { content: "inc" },
                                            { content: "bc1qm*hz0av" },
                                        ],
                                    },

                                ]}
                            />
                        </Card>
                    </div>
                </div>
                <Card
                    title="Stats"
                    body={
                        <div className="substats">
                            <Grid.Row cards>
                                <Grid.Col>
                                    <StatsCard layout={1} movement={5} total={30583 + " $"} label="USD/BTC" />
                                </Grid.Col>
                                <Grid.Col>
                                    <StatsCard layout={1} movement={0} total={42384 + " $"} label="Wallet Value" />
                                </Grid.Col>
                                <Grid.Col>
                                    <StatsCard layout={1} movement={0} total={1.72542} label="Total BTC in Wallet" />
                                </Grid.Col>
                            </Grid.Row>
                        </div>
                    }
                />
            </div>
        )
    }
}

export default Overview;
