import React, { Component } from "react";
import { Alert, Card, colors } from 'tabler-react';
import C3Chart from "react-c3js";
import './Dashboard.scss';

class Overview extends React.Component {
    constructor(props) {
        super(props);
    }

    render() {
        return (
            <div className="dashboard">
                <Card
                    title="Card title"
                    id="chart"
                    body={
                        <C3Chart
                            style={{ height: "10rem" }}
                            data={{
                                columns: [
                                    // each columns data
                                    [
                                        "data1",
                                        0,
                                        5,
                                        1,
                                        2,
                                        7,
                                        5,
                                        6,
                                        8,
                                        24,
                                        7,
                                        12,
                                        5,
                                        6,
                                        3,
                                        2,
                                        2,
                                        6,
                                        30,
                                        10,
                                        10,
                                        15,
                                        14,
                                        47,
                                        65,
                                        55,
                                    ],
                                ],
                                type: "area", // default type of chart
                                groups: [["data1"]],
                                colors: {
                                    data1: colors["blue"],
                                },
                                names: {
                                    // name of each serie
                                    data1: "Purchases",
                                },
                            }}
                            axis={{
                                y: {
                                    padding: {
                                        bottom: 0,
                                    },
                                    show: false,
                                    tick: {
                                        outer: false,
                                    },
                                },
                                x: {
                                    padding: {
                                        left: 0,
                                        right: 0,
                                    },
                                    show: false,
                                },
                            }}
                            legend={{
                                position: "inset",
                                padding: 0,
                                inset: {
                                    anchor: "top-left",
                                    x: 20,
                                    y: 8,
                                    step: 10,
                                },
                            }}
                            tooltip={{
                                format: {
                                    title: function (x) {
                                        return "";
                                    },
                                },
                            }}
                            padding={{
                                bottom: 0,
                                left: -1,
                                right: -1,
                            }}
                            point={{
                                show: false,
                            }}
                        />
                    }
                />
                <Card
                    title="Card title"
                    body={`Lorem ipsum dolor sit amet, consectetur adipisicing elit. Aperiam
            deleniti fugit incidunt, iste, itaque minima neque pariatur
            perferendis sed suscipit velit vitae voluptatem. A consequuntur,
            deserunt eaque error nulla temporibus!`}
                />
                <Alert type="warning" isDismissible>
                    Lorem ipsum dolar sit amet, consectetur adipisicing elit.
        </Alert>
            </div>
        )
    }
}

export default Overview;
