import React, { Component } from "react";
import { Table, Button } from 'tabler-react';
import { Modal } from 'react-bootstrap';

function MyVerticallyCenteredModal(props) {
    return (
        <Modal
            {...props}
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    Modal heading
          </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <h4>Centered Modal</h4>
                <p>
                    Cras mattis consectetur purus sit amet fermentum. Cras justo odio,
                    dapibus ac facilisis in, egestas eget quam. Morbi leo risus, porta ac
                    consectetur ac, vestibulum at eros.
          </p>
            </Modal.Body>
            <Modal.Footer>
                <Button onClick={props.onHide}>Close</Button>
            </Modal.Footer>
        </Modal>
    );
}

class Overview extends React.Component {
    constructor(props) {
        super(props);
        this.value = 1;
        this.USDexchange = 27000;

        /*
        this.value = this.props.value;
        this.USDexchange = this.props.USDexchange;
        */
    }

    USD(value, USDexchange) {
        return value * USDexchange;
    }

    render() {
        return (
            <div className="dashboard">
                <Table highlightRowOnHover={true}>
                    <Table.Header>
                        <Table.ColHeader>Type</Table.ColHeader>
                        <Table.ColHeader>Name</Table.ColHeader>
                        <Table.ColHeader>Address</Table.ColHeader>
                        <Table.ColHeader>Value</Table.ColHeader>
                        <Table.ColHeader>Value in USD</Table.ColHeader>
                        <Table.ColHeader>Actions</Table.ColHeader>
                    </Table.Header>
                    <Table.Body>
                        <Table.Row>
                            <Table.Col>
                                <h4>BTC</h4>
                            </Table.Col>
                            <Table.Col>Long-Time-Storage</Table.Col>
                            <Table.Col>bc1qm6qv2jdgjmt4krahrw9wpcyt5mmt7g8h5hz0av</Table.Col>
                            <Table.Col>{this.value} BTC</Table.Col>
                            <Table.Col>{this.USD(this.value, this.USDexchange)} $</Table.Col>
                            <Table.Col>
                                <Button onClick={this.render(
                                    <MyVerticallyCenteredModal/>
                                )} color="primary">Deposit</Button>
                                <Button color="secondary">Withdraw</Button>
                            </Table.Col>
                        </Table.Row>
                    </Table.Body>
                </Table>
            </div >
        )
    }
}

export default Overview;
